--
-- Struktura tabeli dla tabeli `wp_posts`
--

CREATE TABLE IF NOT EXISTS `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=16 ;

--
-- Zrzut danych tabeli `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-04-14 18:07:12', '2015-04-14 18:07:12', '<p style="text-align: center;">Witaj na blogu Dawida Pianki.</p>\r\n<p style="text-align: center;">Mam nadzieje, że Tobie również się spodoba</p>\r\n <a href="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid1.jpg"><img class="alignnone size-medium wp-image-13" src="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid1-300x298.jpg" alt="dawid" width="300" height="298" /></a>', 'Hej, hej, hej', '', 'publish', 'open', 'open', '', 'witaj-swiecie', '', '', '2015-04-14 18:55:04', '2015-04-14 18:55:04', '', 0, 'http://pianka.cba.pl/?p=1', 0, 'post', '', 0),
(2, 1, '2015-04-14 18:07:12', '2015-04-14 18:07:12', '<p style="text-align: left;">Witam Drodzy Internauci,</p>\r\n<p style="text-align: center;">Blog, który prowadzę jest stworzony z myślą o was jak i moim Doktorze prowadzącym zajęcia z CMS ;) Życze miłego przeglądania i zachęcam do publikacji :D.<a href="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid.jpg"><img class="alignnone size-medium wp-image-11" src="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid-300x298.jpg" alt="dawid" width="300" height="298" /></a></p>', 'Hej, hej drodzy blogerzy', '', 'publish', 'open', 'open', '', 'przykladowa-strona', '', '', '2015-04-14 18:50:42', '2015-04-14 18:50:42', '', 0, 'http://pianka.cba.pl/?page_id=2', 0, 'page', '', 0),
(12, 1, '2015-04-14 18:47:16', '2015-04-14 18:47:16', '<p style="text-align: left;">Witam Drodzy Internauci,</p>\r\n<p style="text-align: center;">Blog, który prowadzę jest stworzony z myślą o was jak i moim Doktorze prowadzącym zajęcia z CMS ;) Życze miłego przeglądania i zachęcam do publikacji :D.<a href="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid.jpg"><img class="alignnone size-medium wp-image-11" src="http://pianka.cba.pl/wp-content/uploads/2015/04/dawid-300x298.jpg" alt="dawid" width="300" height="298" /></a></p>', 'Przykładowa strona', '', 'inherit', 'open', 'open', '', '2-revision-v1', '', '', '2015-04-14 18:47:16', '2015-04-14 18:47:16', '', 2, 'http://pianka.cba.pl/?p=12', 0, 'revision', '', 0),
